import React, {Component} from 'react';

/**
 * This component handles the drawing a new card. It is responsible which step the user is on
 * As well as checking if their guess was correct.
 *
 * @param drawCard
 * @returns {JSX.Element}
 * @constructor
 */
class FetchCard extends Component {
    constructor(props) {
        super(props)
        const deckId = this.props
        this.state = {
            step: 0,
            loading: true,
        }

        const handleDraw = this.handleDraw.bind()
    }

    handleDraw() {
        this.setState({
            loading: true
        })
        this.fetchCard()
            .then(res => this.setState({
                step: this.state.step + 1,
                loading: false
            })).catch(err => console.log(err))
    }
    fetchCard = async () => {
        const url = `https://deckofcardsapi.com/api/deck/${this.props.deckId}/draw?count=1`
        const response = await fetch(url)
        const body = await response.json()

        if (response.status !== 200) {
            throw Error(body.message)
        }

        /** Logs the response */
        console.log(body)
        return body;
    }

    handleReset() {
        this.setState({
            step: 0,
            loading: false
        })
    }

    render() {
        return (
            <div>

            </div>
        );
    }
}

export default FetchCard;
