// Utility class to help with:
// cards -> numerical value
// Cards -> color
export function getColor(cardSuit) {
    switch (cardSuit) {
        case 'CLUBS':
        case 'SPADES':
            return 'black'
        case 'DIAMONDS':
        case 'HEARTS':
            return 'red'
        default:
            return -1
    }
}

export function isHighOrLow(currCard, prevCard, button) {
    return (
        (button === 'high' && currCard.value >= prevCard.value) ||
        (button === 'low' && currCard.value <= prevCard.value)
    )
}

export function isInBetween(cardsList, step) {
    currCardVal = cardsList[step - 1].value
}
