    }

    render() {
        return (
            <div>
                <button onClick={this.handleReset}>Reset</button>
                <div>{this.state.cards.map(card => <Card url={card.image}>{card.image}</Card>)}</div>
                <AnswerSection
                    step={this.state.step}
                    clickHandler={this.clickHandler}/>
            </div>
        );
    }
}

export default FetchCard;
