import React, {Component} from 'react';
import Card from "./Card";
import AnswerSection from "./AnswerSection";

/**
 * This component handles the drawing a new card. It is responsible which step the user is on
 * As well as checking if their guess was correct.
 *
 * @param drawCard
 * @returns {JSX.Element}
 * @constructor
 */
class FetchCard extends Component {
    constructor(props) {
        super(props)
        this.state = {
            step: 0,
            loading: true,
            cards: []
        }
        this.handleDraw = this.handleDraw.bind(this)
        this.handleReset = this.handleReset.bind(this)
        this.clickHandler = this.clickHandler.bind(this)
    }

    handleDraw() {
        console.log("Drawing Card")
        this.fetchCard()
            .then(res => this.setState({
                step: this.state.step + 1,
                loading: false,
                cards: this.state.cards.concat(res.cards[0])
            })).catch(err => console.log(err + "wassap kiddo u dun goofed"))
    }
    fetchCard = async () => {
        const url = `https://deckofcardsapi.com/api/deck/${this.props.deckId}/draw?count=1`
        const response = await fetch(url)
        const body = await response.json()

        if (response.status !== 200) {
            throw Error(body.message)
        }

        /** Logs the response */
        console.log(body)
        return body;
    }

    handleReset() {
        this.setState({
            step: 0,
            loading: false,
            cards: []
        })
    }

    clickHandler(button) {
        this.handleDraw()
        // check if it is correct/incorrect -> check the step, then check what button they pressed
        // 0 -> Color
        // 1 -> High or Low
        // 2 -> In between or outside
        // 3 -> Suit
        // if correct, then draw, else, reset.
    } 

    render() {
        return (
            <div>
                <button onClick={this.handleReset}>Reset</button>
                <div>{this.state.cards.map(card => <Card url={card.image}>{card.image}</Card>)}</div>
                <AnswerSection
                    step={this.state.step}
                    clickHandler={this.clickHandler}/>
            </div>
        );
    }
}

export default FetchCard;
