// Utility class to help with:
// cards -> numerical value
// Cards -> color
export function getColor(cardSuit) {
    switch (cardSuit) {
        case 'CLUBS':
        case 'SPADES':
            return 'black'
        case 'DIAMONDS':
        case 'HEARTS':
            return 'red'
        default:
            return -1
    }
}
