import React, {Component} from 'react';

class Counter extends Component {
    constructor(props) {
        super(props)

        this.state = {
            count: 0
        }
    }

    increment() {
        this.setState({
            count: this.state.count + 1
        }, () => this.stateCallback())
    }

    stateCallback() {
        console.log("Updated state to: " + this.state.count)
    }

    decrement() {
        this.setState({
            count: this.state.count - 1
        }, () => this.stateCallback())
    }

    decrementFive() {
        this.setState({
            count: this.state.count - 1
        }, () => this.stateCallback())
    }

    incrementFive() {
        this.setState({
            count: this.state.count + 5
        }, () => this.stateCallback());
    }

    render() {
        return (
            <div>
                <h1>{this.state.count}</h1>
                <button onClick={() => this.increment()}>Increment</button>
                <br/>
                <button onClick={() => this.decrement()}>Decrement</button>
                <br/>
                <button onClick={() => this.incrementFive()}>Increment Five</button>
                <br/>
                <button onClick={() => this.decrementFive()}>Decrement Five</button>
            </div>
        );
    }


}

export default Counter;
